<?php

namespace BenchmarkingBundle\Repository;

use Doctrine\ORM\EntityManager;
use Doctrine\ORM\QueryBuilder;
use Symfony\Component\Validator\Constraints\DateTime;

/**
 * BoiteVitesseRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class BoiteVitesseRepository extends \Doctrine\ORM\EntityRepository
{
    public function myFindAll(){
        /*
        $queryBuilder = $this->_em->createQueryBuilder()
            ->select('bv')
            ->from($this->_entityName, 'bv')
        ;
        */


        $queryBuilder = $this->createQueryBuilder('bv');
        $query = $queryBuilder->getQuery();
        $results = $query->getResult();

        return $results;
    }

    public function myFindOne($id){
        $qb = $this->createQueryBuilder('bv');
        $qb
            ->where('bv.id = :id')
            ->setParameter('id', $id)
        ;
        return $qb->getQuery()->getResult();
    }


    public function myFindAllDQL(){
        $query = $this->_em->createQuery('SELECT bv FROM BenchmarkingBundle:BoiteVitesse bv');
        $results  = $query->getResult();

        return $results;
    }


}
